package bhanuproj;

public class Ardhamatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int x=300;
         int y=200;
         {
        	 System.out.println(x+y);
        	 System.out.println(x*y);
        	 System.out.println(x-y);
        	 System.out.println(x/y);
        	 System.out.println(x%y);
         }
	}

}
